sudo sysdig -p "* %evt.num %evt.rawtime.s.%evt.rawtime.ns %evt.cpu %proc.name %proc.pid %evt.type %evt.dir %fd.num %fd.type %fd.name %fd.uid %fd.filename %fd.cip %fd.sip %fd.cport %fd.sport %fd.l4proto %evt.args" "(evt.type=read or evt.type=readv or evt.type=write or evt.type=writev or evt.type=fcntl or evt.type=accept or evt.type=execve or evt.type=clone or evt.type=pipe or evt.type=rename or evt.type=renameat or evt.type=renameat2 or evt.type=sendmsg or evt.type=recvmsg) and (proc.name!=gmain and proc.name!=systemd-oomd and proc.name!=gdbus and proc.name!=gnome-shell)" -j > logs.txt